import React from "react";
export default class Model extends React.Component{
    constructor(props) {
        super(props);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {inputData:[]};

    }
    handleOnChange (key,value) {
        if(key=="date"){
            value = new Date(value).toLocaleDateString('en-GB');
            this.state.inputData[key]= value;
        }
        this.state.inputData[key]=value;
    }
    handleSubmit(){
        fetch('http://localhost:8000/api/home?_id=' + this.props.model._id,{
            method: 'PUT',
        }).then( async (response) => {
            let res = response;
            console.log(res);
        });
    }
    render() {
        return(
            <div className="Search">
                <div className="card bg-success">
                    <div className="card-body">
                        <form onSubmit={this.handleSubmit}>
                            <p className="card-text">
                                <input className="form-control" type="text" value = {this.props.model.state} name="state" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="date" value = {this.props.model.date} name="date" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" value = {this.props.model.cases} name="cases" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" pvalue = {this.props.model.deaths} name="deaths" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                            </p>
                            <button className="btn btn-primary" >Search</button>
                        </form>
                    </div> 
                </div>
            </div>
        )
    }
}
