import {useState} from "react";
function Search (){
    let inputData = [];
    const [data,setData] = useState();
    const handleOnChange = (key,value) =>{
        if(key=="date"){
          inputData[key]= new Date(value).toLocaleDateString('en-GB');
        }
        inputData[key]=value;
        console.log(inputData);
    }
    const handleSubmit = (e) => {
        e.preventDefault();
       
        let request = 'http://localhost:8000/api/home?';
        for (const [key, value] of Object.entries(inputData)) {
          if(value){
            console.log(value);
            request+=key+'='+value+'&'
          }
        }
        e.target.reset();
        console.log(request);
        (async () => {
            let response = await fetch(request);
            response = await response.json();
            console.log(response);
            setData(response);
        })();
    }

    return(
        <div className="Search">
            <div className="card bg-success">
          <div className="card-body">
            <form onSubmit={handleSubmit}>
                  <h3 className="card-title">Filters: </h3>
                  <p className="card-text">
                    <input className="form-control" type="text" placeholder="State" name="state" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                    <input className="form-control" type="date" placeholder="Date" name="date" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                    <input className="form-control" type="number" min="0" placeholder="Cases" name="cases" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                    <input className="form-control" type="number" min="0" placeholder="Deaths" name="deaths" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                    <input className="form-control" type="number" min="0" placeholder="Results Per Page" name="resultsPerPage" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                    <input className="form-control" type="number" min="0" placeholder="Page" name="page" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                  </p>
                  <button className="btn btn-primary" >Search</button>
              </form>
                </div> 
            </div>
        </div>
    )
}
export default Search;
