import React from "react";
export default class Search extends React.Component {
    constructor(props) {
        super(props);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {inputData:[]};
    }
    handleOnChange (key,value) {
        if(key=="date"){
            value = new Date(value).toLocaleDateString('en-GB');
            this.state.inputData[key]= value;
        }
        this.state.inputData[key]=value;
        console.log(this.state.inputData);
    }
    handleSubmit (e) {
        
        e.preventDefault();
        
        let request = 'http://localhost:8000/api/home?';
        for (const [key, value] of Object.entries(this.state.inputData)) {
          if(value){
            request+=key+'='+value+'&'
          }
        }
        this.setState({inputData:[]});
        e.target.reset();
        (async () => {
            let response = await fetch(request);
            response = await response.json();
            this.props.searchResults(response);
        })();
    }
    render() {
        return(
            <div className="Search">
                <div className="card bg-success">
                <div class="card-header text-light text-center">Search</div>
                    <div className="card-body">
                        <form onSubmit={this.handleSubmit}>
                            <p className="card-text">
                                <select class="form-select" aria-label="Default select example">
                                    <option selected> Open this select menu</option>
                                    <option value="1" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}>Alabama</option>
                                    <option value="2">Alaska</option>
                                    <option value="3">Arizona</option>
                                    <option value="4">Arkansas</option>
                                    <option value="5">California</option>
                                    <option value="6">Colorado</option>
                                    <option value="7">Connecticut</option>
                                    <option value="8">Delaware</option>
                                    <option value="9">District of Columbia</option>
                                    <option value="10">Florida</option>
                                    <option value="11">Georgia</option>
                                    <option value="12">Guam</option>
                                    <option value="13">Hawaii</option>
                                    <option value="14">Idaho</option>
                                    <option value="15">Illinois</option>
                                    <option value="16">Indiana</option>
                                    <option value="17">Iowa</option>
                                    <option value="18">Kansas</option>
                                    <option value="19">Kentucky</option>
                                    <option value="20">Louisiana</option>
                                    <option value="21">Maine</option>
                                    <option value="22">Maryland</option>
                                    <option value="23">Massachusetts</option>
                                    <option value="24">Michigan</option>
                                    <option value="25">Minnesota</option>
                                    <option value="26">Mississippi</option>
                                    <option value="27">Missouri</option>
                                    <option value="28">Montana</option>
                                    <option value="29">Nebraska</option>
                                    <option value="30">Nevada</option>
                                    <option value="31">New Hampshire</option>
                                    <option value="32">New Jersey</option>
                                    <option value="33">New Mexico</option>
                                    <option value="34">New York</option>
                                    <option value="35">North Carolina</option>
                                    <option value="36">North Dakota</option>
                                    <option value="37">Northern Mariana Islands</option>
                                    <option value="38">Ohio</option>
                                    <option value="39">Oklahoma</option>
                                    <option value="40">Oregon</option>
                                    <option value="41">Pennsylvania</option>
                                    <option value="42">Puerto Rico</option>
                                    <option value="43">Rhode Island</option>
                                    <option value="44">South Carolina</option>
                                    <option value="45">South Dakota</option>
                                    <option value="46">Tennessee</option>
                                    <option value="47">Texas</option>
                                    <option value="48">Utah</option>
                                    <option value="49">Vermont</option>
                                    <option value="50">Virgin Islands</option>
                                    <option value="51">Virginia</option>
                                    <option value="52">Washington</option>
                                    <option value="53">West Virginia</option>
                                    <option value="54">Wisconsin</option>
                                    <option value="55">Wyoming</option>  
                                </select>
                                <div class="form-floating mb-3">
                                    <input type="text" class="form-control" id="floatingInput" placeholder="State" name="state" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                    <label for="floatingInput">Input State:</label>
                                </div>
                                <div class="form-floating mb-3">
                                    <input type="date" class="form-control" id="floatingInput" placeholder="Date" name="date" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                    <label for="floatingInput">Select Date:</label>
                                </div>
                                
                                <div class="form-floating mb-3">
                                    <input type="number" min= "0"class="form-control" id="floatingInput" placeholder="Cases" name="cases" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                    <label for="floatingInput">Input Cases:</label>
                                </div>
                                <div class="form-floating mb-3">
                                    <input type="number" min= "0"class="form-control" id="floatingInput" placeholder="Deaths" name="deaths" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                    <label for="floatingInput">Input Deaths:</label>
                                </div>
                            </p>
                            <button className="btn btn-dark" >Search</button>
                        </form>
                    </div> 
                </div>
            </div>
        )
    }
}
