import React from "react";
import Search from "../models/Search.js";
import Form from "../models/Form.js";
import Data from "../models/Data.js";
import "bootstrap/dist/css/bootstrap.min.css";
export default class Home extends React.Component {
    constructor(){
        super()
        this.state = {isLoaded:false,data:null}
        this.searchAndSetResults = this.searchAndSetResults.bind(this);
    }
    componentDidMount(){
        fetch("http://localhost:8000/api/home")
        .then(res => res.json())
        .then((result) => {
            this.setState({isLoaded: true,data: result});
        })
        .catch(error=>
            {this.setState({isLoaded: true,error});
        })
    }
    componentDidUpdate(){
        console.log(this.state);
    }
    searchAndSetResults(data){
       this.setState({data:data})
    }
    render(){
        if(this.state.data){
            return(
                <div className="Home">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-6">
                                <div><Search searchResults = {this.searchAndSetResults} /></div>
                            </div>
                            <div className="col-lg-6">
                                <div><Form /></div>
                            </div>
                            <div className="col-lg-12">
                            <Data data = {this.state.data}/>
                            </div>
                        </div>
                    </div>
                </div> 
            )
        }
        return(
            <div>{this.state.isLoaded}</div>
        )
    }
}
