import React from "react";
import Search from "../models/Search.js";
import Form from "../models/Form.js";
import Results from "../models/Results.js";
import "bootstrap/dist/css/bootstrap.min.css";
export default class Home extends React.Component {
    constructor(){
        super()
        this.state = {
            isLoaded:false,
            data:null,
        }
        this.searchResults = this.searchResults.bind(this);
        this.setResults = this.setResults.bind(this);
    }
    
    componentDidMount(){
        fetch("http://localhost:8000/api/home").then(res => res.json())
      .then((result) => {this.setState({
            isLoaded: true,
            data: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
    }
    componentDidUpdate(){
        this.setResults();
        console.log("updated");
    }

    searchResults(data){
       this.setState({
           data:data
       });
    }
    setResults(){
        return this.state.data;
    }
    render(){
        if(this.state.data){
            return(
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6">
                            <div><Search searchResults = {this.searchResults} /></div>
                        </div>
                        <div className="col-lg-6">
                            <div><Form /></div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-lg-12">
                            <Results setResults = {this.searchAndSetResults}/>
                        </div>
                    </div>
                </div>
            )
        }
        return(
            
            <div>{this.state.isLoaded}</div>
        )
        
    }
}
