import React from "react";
export default class Search extends React.Component {
    constructor(props) {
        super(props);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {inputData:[]};

    }
    handleOnChange (key,value) {
        if(key=="date"){
            value = new Date(value).toLocaleDateString('en-GB');
            this.state.inputData[key]= value;
        }
        this.state.inputData[key]=value;
    }
    handleSubmit (e) {
        e.preventDefault();
        let request = 'http://localhost:8000/api/home?';
        for (const [key, value] of Object.entries(this.state.inputData)) {
          if(value){
            request+=key+'='+value+'&'
          }
        }
        this.setState({
            inputData:[]
        })
        e.target.reset();
        (async () => {
            let response = await fetch(request);
            response = await response.json();
            this.props.searchResults(response);
        })();
    }
    render() {
        return(
            <div className="Search">
                <div className="card bg-success">
                    <div className="card-body">
                        <form onSubmit={this.handleSubmit}>
                            <p className="card-text">
                                <input className="form-control" type="text" placeholder="State" name="state" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="date" placeholder="Date" name="date" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" placeholder="Cases" name="cases" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" placeholder="Deaths" name="deaths" onChange={(e)=>this.handleOnChange(e.target.name,e.target.value)}/>
                            </p>
                            <button className="btn btn-primary" >Search</button>
                        </form>
                    </div> 
                </div>
            </div>
        )
    }
}
