function Form(){

    const handleSubmit = (e) => {
        e.preventDefault();
    
        fetch('http://localhost:8000/api/home', {
          method: 'POST',
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(blog)
        }).then(() => {
          // history.go(-1);
          history.push('/');
        })
      }


    return(
        <div className="Form">
            <form onSubmit={handleSubmit}>
                <label>State:</label>
                <input type="text" required placeholder="State" onChange={(e) => handleOnChange(e.target.value, e.target.name)}/>
                <input type="date" required placeholder="State" onChange={(e) => handleOnChange(e.target.value, e.target.name)}/>
                <input type="cases" required placeholder="State" onChange={(e) => handleOnChange(e.target.value, e.target.name)}/>
                <input type="deaths" required placeholder="State" onChange={(e) => handleOnChange(e.target.value, e.target.name)}/>
                <button className="btn btn-primary">Add</button>
            </form>
        </div>
    )
}

export default Form;
