import {useState} from 'react'
function Form(){

    const [state,setState] = useState();
    const [date,setDate] = useState();
    const [cases,setCases] = useState();
    const [deaths,setDeaths] = useState();

    const handleSubmit = (e) => {
        e.preventDefault();
        const data = {
            "state":state, 
            "date":date, 
            "cases":cases, 
            "deaths":deaths };
        
    
        fetch('http://localhost:5000/api/home',{
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(data)
        }).then( async (response) => {
            let res = response;
            console.log(res);
        })
        
      }


    return(
        <div className="Form">
            <form onSubmit={handleSubmit}>
                <label>State:</label>
                <input type="text" required placeholder="State" onChange={(e) => setState(e.target.value)}/>
                <label>Date:</label>
                <input type="date" required placeholder="Date" onChange={(e) => setDate(e.target.value)}/>
                <label>Cases:</label>
                <input type="number" min = "0" required placeholder="Cases" onChange={(e) => setCases(e.target.value)}/>
                <label>Deaths:</label>
                <input type="number" min = "0" required placeholder="Cases" onChange={(e) => setDeaths(e.target.value)}/>
                <button className="btn btn-primary">Add</button>
            </form>
        </div>
    )
}

export default Form;
