import React from "react";
export default class Search extends React.Component {
    constructor(props) {
        super(props);
        console.log(props);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }



    handleOnChange = (key,value) =>{
        let inputData = [];
        if(key=="date"){
            value = new Date(value).toLocaleDateString('en-GB');
            inputData[key]= value;
          console.log(value);
        }
        inputData[key]=value;
        console.log(inputData);
    }
    handleSubmit = (e) => {
        e.preventDefault();
        let request = 'http://localhost:8000/api/home?';
        for (const [key, value] of Object.entries(inputData)) {
          if(value){
              console.log(value);
            request+=key+'='+value+'&'
          }
        }
        e.target.reset();
        console.log(request);
        (async () => {
            let response = await fetch(request);
            response = await response.json();
            console.log(response);
            setState(response.state)
            setDate(response.date)
            setCases(response.cases)
            setDeaths(response.deaths)
        })();
    }
    render() {
        return(
            <div className="Search">
                <div className="card bg-success">
                    <div className="card-body">
                        <form onSubmit={handleSubmit}>
                            <p className="card-text">
                                <input className="form-control" type="text" placeholder="State" name="state" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="date" placeholder="Date" name="date" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" placeholder="Cases" name="cases" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                                <input className="form-control" type="number" min="0" placeholder="Deaths" name="deaths" onChange={(e)=>handleOnChange(e.target.name,e.target.value)}/>
                            </p>
                            <button className="btn btn-primary" >Search</button>
                        </form>
                    </div> 
                </div>
            </div>
        )
    }
}
