import React from "react";
export default class Model extends React.Component{
    constructor(props) {
        super(props);
        this.handleOnChange = this.handleOnChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {inputData:[]};

    }
    handleOnChange (key,value) {
        if(key=="date"){
            value = new Date(value).toLocaleDateString('en-GB');
            this.state.inputData[key]= value;
        }
        this.state.inputData[key]=value;
    }
    handleSubmit(){
        fetch('http://localhost:8000/api/home?_id=' + this.props.model._id,{
            method: 'DELETE',
        }).then( async (response) => {
            let res = response;
            console.log(res);
        });
    }
    render() {
        return (
            <div class="card">
                <form onSubmit={this.handleSubmit}>
                    <ul class="list-group list-group-flush">
                        {this.props.model.state && <li class="list-group-item"><label>State:{this.props.model.state}</label></li>}
                        {this.props.model.date && <li class="list-group-item"><label>Date:{this.props.model.date}</label></li>}
                        {this.props.model.cases && <li class="list-group-item"><label>Cases:{this.props.model.cases}</label></li>}
                        {this.props.model.deaths && <li class="list-group-item"><label>Deaths:{this.props.model.deaths}</label></li>}
                    </ul>
                    <button className = "btn btn-danger">Delete</button>
                </form>
            </div>
        )
    }
}
